package com.backend.util;

import com.itextpdf.text.*;
import com.itextpdf.text.pdf.*;
import com.itextpdf.text.pdf.codec.Base64;
import lombok.extern.slf4j.Slf4j;

import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;

@Slf4j
public class WatermarkStamper {
    public static void addWaterMark(String pdfSourceURL) throws IOException, DocumentException {
        Document document = new Document();
        File file = new File(pdfSourceURL);
        FileInputStream fileInputStream = new FileInputStream(file);
        PdfReader reader = new PdfReader(fileInputStream);
        PdfStamper stamper = new PdfStamper(reader, new FileOutputStream(pdfSourceURL));
        // image base64 logo
        Image img = getImageFile();
        float w = img.getScaledWidth();
        float h = img.getScaledHeight();
        // properties
        PdfContentByte over;
        Rectangle pagesize;
        float x, y;
        // loop over every page
        int n = reader.getNumberOfPages();
        for (int i = 1; i <= n; i++) {
            // get page size and position
            pagesize = reader.getPageSizeWithRotation(i);
            x = (pagesize.getLeft() + pagesize.getRight()) / 2;
            y = (pagesize.getTop() + pagesize.getBottom()) / 2;
            over = stamper.getOverContent(i);
            over.saveState();
            // set transparency
            PdfGState state = new PdfGState();
            state.setFillOpacity(0.1f);
            over.setGState(state);
            over.addImage(img, w, 0, 0, h, x - (w / 2), y - (h / 2));
            over.restoreState();
        }
        stamper.close();
        reader.close();
    }

    public static Image getImageFile() {
        String base64Image = "";
        Image img = null;
        try {
            img = Image.getInstance(Base64.decode(base64Image));
        } catch (BadElementException e) {
            log.info("Error message:{}", e.getMessage());
        } catch (IOException e) {
            log.info("Error message:{}", e.getMessage());
        }
        return img;
    }
}